import React, { useState } from "react";

function renderShape(shape, i) {
  const { type, x, y, width, height, color } = shape;
  switch (type) {
    case "Rectangle":
      return <rect key={i} x={x} y={y} width={width} height={height} fill={color} stroke="#333" />;
    case "Square":
      return <rect key={i} x={x} y={y} width={width} height={width} fill={color} stroke="#333" />;
    case "Circle":
      return <circle key={i} cx={x + width / 2} cy={y + width / 2} r={width / 2} fill={color} stroke="#333" />;
    case "Line":
      return <line key={i} x1={x} y1={y} x2={x + width} y2={y + height} stroke={color} strokeWidth="3" />;
    case "Triangle":
      return (
        <polygon
          key={i}
          points={`
            ${x + width / 2},${y}
            ${x},${y + height}
            ${x + width},${y + height}
          `}
          fill={color}
          stroke="#333"
        />
      );
    default:
      return null;
  }
}

export default function App() {
  const [drawings, setDrawings] = useState([
    { description: "ציור ראשון", shapes: [] }
  ]);
  const [selectedDrawingIdx, setSelectedDrawingIdx] = useState(0);
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);

  // שליחת הודעה לשרת
  async function handleSend() {
    if (!prompt.trim()) return;
    setMessages([...messages, { from: "user", text: prompt }]);
    setLoading(true);

    try {
      const response = await fetch("/api/drawings/add-shape", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt,
          existingDrawings: drawings
        })
      });

      if (!response.ok) {
        setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בשליחת הבקשה לשרת" }]);
        setLoading(false);
        return;
      }

      const newShapes = await response.json();

      // הוספת הצורות החדשות לציור הנבחר
      setDrawings(ds => ds.map((drawing, i) =>
        i === selectedDrawingIdx
          ? { ...drawing, shapes: [...drawing.shapes, ...newShapes] }
          : drawing
      ));

      setMessages(msgs => [...msgs, { from: "bot", text: "הציור התעדכן בהצלחה!" }]);
      setPrompt("");
    } catch (err) {
      setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בתקשורת עם השרת" }]);
    }
    setLoading(false);
  }

  // הוספת ציור חדש
  function handleNewDrawing() {
    setDrawings([...drawings, { description: `ציור ${drawings.length + 1}`, shapes: [] }]);
    setSelectedDrawingIdx(drawings.length);
  }

  // בחירת ציור
  function handleSelectDrawing(idx) {
    setSelectedDrawingIdx(idx);
  }

  return (
    <div style={{ direction: "rtl", padding: 30 }}>
      <h1>AI Drawer</h1>
      <div style={{ marginBottom: 10 }}>
        <select value={selectedDrawingIdx} onChange={e => handleSelectDrawing(Number(e.target.value))}>
          {drawings.map((drawing, idx) => (
            <option key={idx} value={idx}>
              {drawing.description || `ציור ${idx + 1}`}
            </option>
          ))}
        </select>
        <button onClick={handleNewDrawing}>+ ציור חדש</button>
      </div>
      <div style={{ display: "flex", gap: 30 }}>
        <div style={{ flex: 1 }}>
          <div style={{ border: "1px solid #ccc", minHeight: 300, padding: 10, background: "#fafafa" }}>
            <div style={{ fontWeight: "bold", marginBottom: 10 }}>היסטוריית שיחה:</div>
            {messages.map((msg, i) => (
              <div key={i} style={{
                textAlign: msg.from === "user" ? "right" : "left",
                color: msg.from === "user" ? "#1976d2" : "#388e3c",
                margin: "5px 0"
              }}>
                <b>{msg.from === "user" ? "אתה:" : "בוט:"}</b> {msg.text}
              </div>
            ))}
          </div>
          <div style={{ marginTop: 10, display: "flex", gap: 5 }}>
            <input
              type="text"
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              placeholder="כתוב הודעה לבוט..."
              disabled={loading}
              style={{ flex: 1 }}
              onKeyDown={e => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend} disabled={loading || !prompt.trim()}>שלח</button>
          </div>
        </div>
        <div style={{ flex: 1 }}>
          <svg width="600" height="400" style={{ background: "#fff", border: "1px solid #ccc" }}>
            {drawings[selectedDrawingIdx].shapes.map((shape, i) => renderShape(shape, i))}
          </svg>
        </div>
      </div>
    </div>
  );
}
