function renderShape(shape, i) {
  const { type, x, y, width, height, color } = shape;
  switch (type) {
    case "Rectangle":
      return <rect key={i} x={x} y={y} width={width} height={height} fill={color} stroke="#333" />;
    case "Square":
      return <rect key={i} x={x} y={y} width={width} height={width} fill={color} stroke="#333" />;
    case "Circle":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + width / 2}
          rx={width / 2}
          ry={width / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Ellipse":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + height / 2}
          rx={width / 2}
          ry={height / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Line":
      return <line key={i} x1={x} y1={y} x2={x + width} y2={y + height} stroke={color} strokeWidth="3" />;
    case "Triangle":
      return (
        <polygon
          key={i}
          points={`
            ${x + width / 2},${y}
            ${x},${y + height}
            ${x + width},${y + height}
          `}
          fill={color}
          stroke="#333"
        />
      );
    default:
      return null;
  }
}

export default function App() {
  const [canvases, setCanvases] = useState([
    { id: null, name: "new canvas", drawings: [] }
  ]);

  const [selectedCanvasIdx, setSelectedCanvasIdx] = useState(0);
  const [drawings, setDrawings] = useState(canvases[0].drawings);
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [canvasName, setCanvasName] = useState("");

  // עדכון סטייטים כאשר מחליפים קנבס
  useEffect(() => {
    const canvasData = canvases[selectedCanvasIdx];
    setDrawings(canvasData.drawings);
    setUndoStack([]);
    setRedoStack([]);
    setMessages([]);
  }, [selectedCanvasIdx, canvases]);

  function handleClearDrawing() {
    setUndoStack(prevUndo => [...prevUndo, drawings]);
    setRedoStack([]);

    setDrawings([]);
  }

  // Undo
  function handleUndo() {
    console.log("Undo Stack before:", undoStack);

    if (undoStack.length === 0) {
      console.log("Cannot undo, undoStack is empty.");
      return;
    }

    setUndoStack(prevUndo => {
      const stateToRestore = prevUndo[prevUndo.length - 1];
      const currentState = drawings;

      setRedoStack(prevRedo => [currentState, ...prevRedo]);

      const newUndo = prevUndo.slice(0, -1);

      setDrawings(stateToRestore);

      console.log("Undo Stack after:", newUndo);
      return newUndo;
    });
  }

  function handleRedo() {
    console.log("Redo Stack before:", redoStack);

    if (redoStack.length === 0) {
      console.log("Cannot redo, redoStack is empty.");
      return;
    }

    setRedoStack(prevRedo => {
      const [stateToRestore, ...restRedo] = prevRedo;
      const currentState = drawings;

      setUndoStack(prevUndo => [...prevUndo, currentState]);

      setDrawings(stateToRestore);

      console.log("Redo Stack after:", restRedo);
      return restRedo;
    });
  }

  // פתיחת דיאלוג שמירה
  function handleSave() {
    setCanvasName("");
    setShowSaveDialog(true);
  }

  // שמירת קנבס לשרת (מקבל id מהשרת)
  async function handleSaveCanvas() {
    if (!canvasName.trim()) return;
    setShowSaveDialog(false);
    try {
      const response = await fetch("/api/drawings/save-canvas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: canvasName,
          drawings: drawings
        })
      });

      if (response.ok) {
        const saved = await response.json();
        const newSavedCanvas = {
          id: saved.id,
          name: saved.name,
          drawings: drawings,
        };

        setCanvases(prev => {
          let updatedCanvases = [...prev];
          const mainCanvasIdx = prev.findIndex(canvas => canvas.id === null && canvas.name === "new canvas");

          // אם הקנבס הנוכחי היה ה"קנבס ראשי" ללא ID, נעדכן אותו עם ה-ID והשם החדשים
          // במקום להוסיף אותו כקנבס חדש.
          if (selectedCanvasIdx === mainCanvasIdx && newSavedCanvas.id !== null) {
            updatedCanvases[selectedCanvasIdx] = newSavedCanvas;
          } else {
            // אחרת, נוסיף אותו כקנבס חדש לרשימה (זה המקרה של שמירה בשם אחר, או שמירה של קנבס שכבר נטען)
            updatedCanvases = [...prev, newSavedCanvas];
          }
          return updatedCanvases;
        });

        // **התיקון המרכזי כאן:**
        // יצירת קנבס חדש וריק ובחירתו כקנבס הפעיל.
        setCanvases(prev => {
          const newEmptyCanvas = { id: null, name: "new canvas", drawings: [] };
          const updatedCanvases = [...prev, newEmptyCanvas];
          setSelectedCanvasIdx(updatedCanvases.length - 1); // בחירת הקנבס החדש
          return updatedCanvases;
        });

        alert("הקנבס נשמר בהצלחה! עובר לקנבס חדש.");
      } else {
        alert("שגיאה בשמירת הקנבס");
      }
    } catch (err) {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  async function handleSelectCanvas(idx) {
    setSelectedCanvasIdx(idx);
    const canvas = canvases[idx];

    if (!canvas.id) {
      setDrawings(canvas.drawings);
      setUndoStack([]);
      setRedoStack([]);
      setMessages([]);
      return;
    }

    try {
      const res = await fetch(`/api/drawings/get/${canvas.id}`);
      if (res.ok) {
        const data = await res.json();
        setDrawings(data.drawings);
        setUndoStack([]);
        setRedoStack([]);
        setMessages([]);
      } else {
        alert("שגיאה בטעינת הקנבס");
      }
    } catch {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  // שליחת הודעה לשרת
  async function handleSend() {
    if (!prompt.trim()) return;
    setMessages([...messages, { from: "user", text: prompt }]);
    setLoading(true);

    try {
      const response = await fetch("/api/drawings/add-draw", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt,
          existingDrawings: drawings
        })
      });

      if (!response.ok) {
        setMessages(msgs => [...msgs, { from: "bot", text: "Error communicating with the server" }]);
        setLoading(false);
        return;
      }

      const newShapes = await response.json();

      setUndoStack(prevUndo => [...prevUndo, drawings]);
      setRedoStack([]);

      setDrawings(prev => {
        const newDrawing = {
          description: prompt,
          shapes: newShapes
        };
        return [...prev, newDrawing];
      });

      setMessages(msgs => [...msgs, { from: "bot", text: "Drawing added successfully!" }]);
      setPrompt("");
    } catch (err) {
      setMessages(msgs => [...msgs, { from: "bot", text: "Error communicating with the server" }]);
    }
    setLoading(false);
  }

  return (
    <div className="app-container">
      <div className="toolbar">

        <select value={selectedCanvasIdx} onChange={e => handleSelectCanvas(Number(e.target.value))}>
          {canvases.map((canvas, idx) => (
            <option key={canvas.id ?? idx} value={idx}>{canvas.name}</option>
          ))}
        </select>
        <button
          className="toolbar-btn orange"
          onClick={handleUndo}
          disabled={undoStack.length === 0}
        >
          undo
        </button>
        <button
          className="toolbar-btn purple"
          onClick={handleRedo}
          disabled={redoStack.length === 0}
        >
          redo
        </button>
        {/* כפתור ניקוי ציור */}
        <button className="toolbar-btn red" onClick={handleClearDrawing}>clear</button>

        <button className="toolbar-btn green" onClick={handleSave}>save</button>
      </div>
      <div className="main-content">
        <div className="chat-section">
          <div className="chat-title">chat history</div>
          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div key={i} className={`chat-msg ${msg.from}`}>
                <b>{msg.from === "user" ? "you:" : "bot:"}</b> {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-row">
            <input
              type="text"
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              placeholder="write a message..."
              disabled={loading}
              onKeyDown={e => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend} disabled={loading || !prompt.trim()}>send</button>
          </div>
        </div>
        <div className="canvas-section">
          {loading && <div className="loading-indicator">מצייר לך...</div>}
          <svg width="600" height="400">
            {drawings.flatMap((drawing, dIdx) =>
              drawing.shapes.map((shape, i) => renderShape(shape, `${dIdx}-${i}`))
            )}
          </svg>
        </div>
      </div>

      {/* דיאלוג שמירה */}
      {showSaveDialog && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000
        }}>
          <div style={{
            background: "#fff", padding: 30, borderRadius: 10, boxShadow: "0 2px 12px #0002", minWidth: 300
          }}>
            <div style={{ marginBottom: 10, fontWeight: "bold" }}>canvas name:</div>
            <input
              type="text"
              value={canvasName}
              onChange={e => setCanvasName(e.target.value)}
              placeholder="insert canvas name"
              style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc", marginBottom: 16 }}
              autoFocus
            />
            <div style={{ display: "flex", gap: 10, justifyContent: "flex-end" }}>
              <button className="toolbar-btn green" onClick={handleSaveCanvas} disabled={!canvasName.trim()}>שמור</button>
              <button className="toolbar-btn orange" onClick={() => setShowSaveDialog(false)}>ביטול</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
