import React, { useState } from "react";
import "./App.css";

const allowedTypes = ["Circle", "Line", "Rectangle", "Square", "Triangle"];

export default function App() {
  const [messages, setMessages] = useState([
    { from: "user", text: "draw sun and grass with tree" },
    { from: "bot", text: "ציור נוסף בוצע בהצלחה!" },
  ]);
  const [input, setInput] = useState("");
  const [drawingId, setDrawingId] = useState(10);
  const [shapes, setShapes] = useState([]);
  const [loading, setLoading] = useState(false);

  // שליחת בקשה לשרת
  const handleSend = async () => {
    if (!input.trim()) return;
    setMessages([...messages, { from: "user", text: input }]);
    setInput("");
    setLoading(true);

    try {
    console.log("");

      const response = await fetch("/api/drawings/add-shape", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: input,
          existingShapes: shapes,
        }),
      });

      if (!response.ok) {
        setMessages((msgs) => [
          ...msgs,
          { from: "bot", text: "שגיאה בשליחת הבקשה לשרת" },
        ]);
        setLoading(false);
        return;
      }
      console.log(response);

      const newShapes = await response.json();

      // סינון הצורות החוקיות בלבד
      const filteredShapes = newShapes.filter((shape) =>
        allowedTypes.includes(shape.type)
      );

      setShapes((prev) => [...prev, ...filteredShapes]);
      setMessages((msgs) => [
        ...msgs,
        { from: "bot", text: "הציור התווסף בהצלחה!" },
      ]);
    } catch (err) {
      setMessages((msgs) => [
        ...msgs,
        { from: "bot", text: "שגיאה בתקשורת עם השרת" },
      ]);
    }
    setLoading(false);
  };

  // ציור הצורות על ה-SVG
  const renderShape = (shape, i) => {
    const { type, x, y, width, height, color } = shape;
    switch (type) {
      case "Rectangle":
        return (
          <rect
            key={i}
            x={x}
            y={y}
            width={width}
            height={height}
            fill={color}
            stroke="#333"
          />
        );
      case "Square":
        return (
          <rect
            key={i}
            x={x}
            y={y}
            width={width}
            height={width}
            fill={color}
            stroke="#333"
          />
        );
      case "Circle":
        return (
          <circle
            key={i}
            cx={x + width / 2}
            cy={y + width / 2}
            r={width / 2}
            fill={color}
            stroke="#333"
          />
        );
      case "Line":
        return (
          <line
            key={i}
            x1={x}
            y1={y}
            x2={x + width}
            y2={y + height}
            stroke={color}
            strokeWidth="3"
          />
        );
      case "Triangle":
        // ציור משולש שווה-צלעות
        return (
          <polygon
            key={i}
            points={`
              ${x + width / 2},${y}
              ${x},${y + height}
              ${x + width},${y + height}
            `}
            fill={color}
            stroke="#333"
          />
        );
      default:
        return null;
    }
  };

  // ניקוי הקנבס
  const handleClear = () => setShapes([]);

  return (
    <div className="app-container">
      <div className="toolbar">
        <select
          value={drawingId}
          onChange={(e) => setDrawingId(Number(e.target.value))}
        >
          <option value={10}>Drawing #10</option>
          <option value={11}>Drawing #11</option>
        </select>
        <button className="toolbar-btn blue">+ New Drawing</button>
        <button className="toolbar-btn green" onClick={handleSend} disabled={loading || !input.trim()}>
          Send
        </button>
        <button className="toolbar-btn yellow">Undo</button>
        <button className="toolbar-btn orange" onClick={handleClear}>
          Clear
        </button>
        <button className="toolbar-btn purple">Save</button>
      </div>
      <div className="main-content">
        <div className="chat-section">
          <div className="chat-title">your chat with the bot</div>
          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`chat-msg ${msg.from === "user" ? "user" : "bot"}`}
              >
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-row">
            <input
              type="text"
              placeholder="write a message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSend()}
              disabled={loading}
            />
            <button onClick={handleSend} disabled={loading || !input.trim()}>
              send
            </button>
          </div>
        </div>
        <div className="canvas-section">
          <svg width="600" height="400" style={{ background: "#fff" }}>
            {shapes.map((shape, i) => renderShape(shape, i))}
          </svg>
        </div>
      </div>
    </div>
  );
}
