import React, { useState, useEffect } from "react";
import "./App.css";

function renderShape(shape, i) {
  const { type, x, y, width, height, color } = shape;
  switch (type) {
    case "Rectangle":
      return <rect key={i} x={x} y={y} width={width} height={height} fill={color} stroke="#333" />;
    case "Square":
      return <rect key={i} x={x} y={y} width={width} height={width} fill={color} stroke="#333" />;
    case "Circle":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + width / 2}
          rx={width / 2}
          ry={width / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Ellipse":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + height / 2}
          rx={width / 2}
          ry={height / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Line":
      return <line key={i} x1={x} y1={y} x2={x + width} y2={y + height} stroke={color} strokeWidth="3" />;
    case "Triangle":
      return (
        <polygon
          key={i}
          points={`
            ${x + width / 2},${y}
            ${x},${y + height}
            ${x + width},${y + height}
          `}
          fill={color}
          stroke="#333"
        />
      );
    default:
      return null;
  }
}

export default function App() {
  const [canvases, setCanvases] = useState([
    { id: null, name: "קנבס ראשי", drawings: [{ description: "ציור ראשון", shapes: [] }] }
  ]);
  const [selectedCanvasIdx, setSelectedCanvasIdx] = useState(0);

  const [drawings, setDrawings] = useState(canvases[0].drawings);

  // Undo ו-Redo הם עבור כל ציור בקנבס
  const [selectedDrawingIdx, setSelectedDrawingIdx] = useState(0);
  const [undoStack, setUndoStack] = useState([[[]]]);
  const [redoStack, setRedoStack] = useState([[]]);

  // שאר הסטייטים
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [canvasName, setCanvasName] = useState("");

  // עדכון סטייטים כאשר מחליפים קנבס
  useEffect(() => {
    setDrawings(canvases[selectedCanvasIdx].drawings);
    setSelectedDrawingIdx(0);
    setUndoStack([[[]]]);
    setRedoStack([[]]);
    setMessages([]);
  }, [selectedCanvasIdx, canvases]);

  // עדכון undoStack כאשר מוסיפים ציור חדש
  useEffect(() => {
    if (undoStack.length < drawings.length) {
      setUndoStack([...undoStack, [[]]]);
      setRedoStack([...redoStack, []]);
    }
  }, [drawings, undoStack, redoStack]);

  // ניקוי הציור הנבחר
  function handleClearDrawing() {
    // שומר אנדו לניקוי
    setUndoStack(undoStack.map((stack, i) =>
      i === selectedDrawingIdx ? [...stack, drawings[selectedDrawingIdx].shapes] : stack
    ));
    setRedoStack(redoStack.map((stack, i) =>
      i === selectedDrawingIdx ? [] : stack
    ));

    setDrawings(drawings.map((drawing, i) =>
      i === selectedDrawingIdx ? { ...drawing, shapes: [] } : drawing
    ));
  }


  // Undo
  function handleUndo() {
    if (undoStack[selectedDrawingIdx].length <= 1) return;
    const newUndoStack = undoStack.map((stack, i) =>
      i === selectedDrawingIdx ? stack.slice(0, -1) : stack
    );
    const prevShapes = newUndoStack[selectedDrawingIdx][newUndoStack[selectedDrawingIdx].length - 1] || [];
    setUndoStack(newUndoStack);
    setRedoStack(redoStack.map((stack, i) =>
      i === selectedDrawingIdx
        ? [drawings[selectedDrawingIdx].shapes, ...stack]
        : stack
    ));
    setDrawings(drawings.map((drawing, i) =>
      i === selectedDrawingIdx
        ? { ...drawing, shapes: prevShapes }
        : drawing
    ));
  }

  // Redo
  function handleRedo() {
    if (redoStack[selectedDrawingIdx].length === 0) return;
    const [nextShapes, ...restRedo] = redoStack[selectedDrawingIdx];
    setRedoStack(redoStack.map((stack, i) =>
      i === selectedDrawingIdx ? restRedo : stack
    ));
    setUndoStack(undoStack.map((stack, i) =>
      i === selectedDrawingIdx
        ? [...stack, nextShapes]
        : stack
    ));
    setDrawings(drawings.map((drawing, i) =>
      i === selectedDrawingIdx
        ? { ...drawing, shapes: nextShapes }
        : drawing
    ));
  }

  // פתיחת דיאלוג שמירה
  function handleSave() {
    setCanvasName("");
    setShowSaveDialog(true);
  }

  // שמירת קנבס לשרת (מקבל id מהשרת)
  async function handleSaveCanvas() {
    if (!canvasName.trim()) return;
    setShowSaveDialog(false);
    try {
      const response = await fetch("/api/drawings/save-canvas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: canvasName,
          drawings: drawings 
        })
      });

      if (response.ok) {
        const saved = await response.json();
        const newCanvas = {
          id: saved.id,
          name: saved.name,
          drawings: [{ description: "ציור ראשון", shapes: [] }]
        };

        setCanvases(prev => [...prev, newCanvas]);
        setSelectedCanvasIdx(0);

        alert("הקנבס נשמר בהצלחה!");
      } else {
        alert("שגיאה בשמירת הקנבס");
      }
    } catch (err) {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  async function handleSelectCanvas(idx) {
    setSelectedCanvasIdx(idx);
    const canvas = canvases[idx];
    if (!canvas.id) {
      setDrawings(canvas.drawings);
      setSelectedDrawingIdx(0);
      setUndoStack([[[]]]);
      setRedoStack([[]]);
      return;
    }

    try {
      const res = await fetch(`/api/drawings/get/${canvas.id}`);
      if (res.ok) {
        const data = await res.json();
        setDrawings(data.drawings);
        setSelectedDrawingIdx(0);
        setUndoStack([[[]]]);
        setRedoStack([[]]);
      } else {
        alert("שגיאה בטעינת הקנבס");
      }
    } catch {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  // שליחת הודעה לשרת
  async function handleSend() {
    if (!prompt.trim()) return;
    setMessages([...messages, { from: "user", text: prompt }]);
    setLoading(true);

    try {
      const response = await fetch("/api/drawings/add-draw", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt,
          existingDrawings: drawings
        })
      });

      if (!response.ok) {
        setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בשליחת הבקשה לשרת" }]);
        setLoading(false);
        return;
      }

      const newShapes = await response.json();

      setUndoStack(undoStack.map((stack, i) =>
        i === selectedDrawingIdx
          ? [...stack, [...drawings[selectedDrawingIdx].shapes, ...newShapes]]
          : stack
      ));
      setRedoStack(redoStack.map((stack, i) =>
        i === selectedDrawingIdx ? [] : stack
      ));

      setDrawings(ds => ds.map((drawing, i) =>
        i === selectedDrawingIdx
          ? { ...drawing, shapes: [...drawing.shapes, ...newShapes] }
          : drawing
      ));

      setMessages(msgs => [...msgs, { from: "bot", text: "הציור התעדכן בהצלחה!" }]);
      setPrompt("");
    } catch (err) {
      setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בתקשורת עם השרת" }]);
    }
    setLoading(false);
  }

  return (
    <div className="app-container">
      <div className="toolbar">
        {/* בחירת קנבס אחד בלבד */}
        <select value={selectedCanvasIdx} onChange={e => handleSelectCanvas(Number(e.target.value))}>
          {canvases.map((canvas, idx) => (
            <option key={canvas.id ?? idx} value={idx}>{canvas.name}</option>
          ))}
        </select>
        {/* בחירת ציור נשארת כי עדיין צריך לבחור איזה ציור לערוך */}
        {/* <select value={selectedDrawingIdx} onChange={e => handleSelectDrawing(Number(e.target.value))}>
          {drawings.map((drawing, idx) => (
            <option key={idx} value={idx}>
              {drawing.description || `ציור ${idx + 1}`}
            </option>
          ))}
        </select> */}

        {/* <button className="toolbar-btn blue" onClick={handleNewDrawing}>+ ציור חדש</button> */}
        <button className="toolbar-btn orange" onClick={handleUndo} disabled={undoStack[selectedDrawingIdx]?.length <= 1}>אנדו</button>
        <button className="toolbar-btn purple" onClick={handleRedo} disabled={redoStack[selectedDrawingIdx]?.length === 0}>רדו</button>

        {/* כפתור ניקוי ציור */}
        <button className="toolbar-btn red" onClick={handleClearDrawing}>ניקוי לוח</button>

        <button className="toolbar-btn green" onClick={handleSave}>סייב</button>
      </div>
      <div className="main-content">
        <div className="chat-section">
          <div className="chat-title">היסטוריית שיחה</div>
          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div key={i} className={`chat-msg ${msg.from}`}>
                <b>{msg.from === "user" ? "אתה:" : "בוט:"}</b> {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-row">
            <input
              type="text"
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              placeholder="כתוב הודעה לבוט..."
              disabled={loading}
              onKeyDown={e => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend} disabled={loading || !prompt.trim()}>שלח</button>
          </div>
        </div>
                  {loading && <div className="loading-indicator">מצייר לך...</div>}

        <div className="canvas-section">
          <svg width="600" height="400">
            {drawings[selectedDrawingIdx]?.shapes.map((shape, i) => renderShape(shape, i))}
          </svg>
        </div>
      </div>

      {/* דיאלוג שמירה */}
      {showSaveDialog && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000
        }}>
          <div style={{
            background: "#fff", padding: 30, borderRadius: 10, boxShadow: "0 2px 12px #0002", minWidth: 300
          }}>
            <div style={{ marginBottom: 10, fontWeight: "bold" }}>שם הקנבס:</div>
            <input
              type="text"
              value={canvasName}
              onChange={e => setCanvasName(e.target.value)}
              placeholder="הכנס שם לקנבס"
              style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc", marginBottom: 16 }}
              autoFocus
            />
            <div style={{ display: "flex", gap: 10, justifyContent: "flex-end" }}>
              <button className="toolbar-btn green" onClick={handleSaveCanvas} disabled={!canvasName.trim()}>שמור</button>
              <button className="toolbar-btn orange" onClick={() => setShowSaveDialog(false)}>ביטול</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
