      canvasToUpdate.drawings = newDrawingsArray;
      canvasToUpdate.undoStack = newUndoStack;
      canvasToUpdate.redoStack = newRedoStack;
      updatedCanvases[selectedCanvasIdx] = canvasToUpdate;
      return updatedCanvases;
