          // או מוסיפים אותו כקנבס חדש אם לא היה לו ID קודם.
          newCanvases[selectedCanvasIdx] = {
            ...newCanvases[selectedCanvasIdx],
            id: saved.id,
            name: saved.name
            // Drawings, undoStack, redoStack נשארים כמו שהיו
          };
          return newCanvases;
        });
        alert("הקנבס נשמר בהצלחה!");
      } else {
        alert("שגיאה בשמירת הקנבס");
      }
    } catch (err) {
      alert("שגיאה בתקשורת עם השרת");
    }
  }
  async function handleSelectCanvas(idx) {
    setSelectedCanvasIdx(idx);
