import React, { useState, useEffect } from "react";
import "./App.css";

function renderShape(shape, i) {
  const { type, x, y, width, height, color } = shape;
  switch (type) {
    case "Rectangle":
      return <rect key={i} x={x} y={y} width={width} height={height} fill={color} stroke="#333" />;
    case "Square":
      return <rect key={i} x={x} y={y} width={width} height={width} fill={color} stroke="#333" />;
    case "Circle":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + width / 2}
          rx={width / 2}
          ry={width / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Ellipse":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + height / 2}
          rx={width / 2}
          ry={height / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Line":
      return <line key={i} x1={x} y1={y} x2={x + width} y2={y + height} stroke={color} strokeWidth="3" />;
    case "Triangle":
      return (
        <polygon
          key={i}
          points={`
            ${x + width / 2},${y}
            ${x},${y + height}
            ${x + width},${y + height}
          `}
          fill={color}
          stroke="#333"
        />
      );
    default:
      return null;
  }
}

export default function App() {
  const [canvases, setCanvases] = useState([
    // הקנבס הראשי, שמכיל מערך drawings, כרגע עם ציור אחד בלבד.
    // ה-undoStack וה-redoStack יהיו ישירות בקנבס, כי הם משמשים לכל הצורות על הקנבס הזה.
    {
      id: null,
      name: "קנבס ראשי",
      drawings: [], // יכיל את כל הציורים שנוצרו בפרומפטים
      undoStack: [[]], // היסטוריית צורות הקנבס הראשי
      redoStack: [],  // היסטוריית רדו של צורות הקנבס הראשי
    },
  ]);
  const [selectedCanvasIdx, setSelectedCanvasIdx] = useState(0);

  // drawings הוא מערך הציורים של הקנבס הנבחר כרגע.
  // אנו משתמשים ב-drawings[0].shapes כדי להציג את כל הצורות על הקנבס.
  const currentCanvas = canvases[selectedCanvasIdx];
  const currentDrawings = currentCanvas.drawings; // כל הציורים שנוצרו בפרומפטים
  const currentShapes = currentDrawings.flatMap(drawing => drawing.shapes); // כל הצורות של כל הציורים יחד
  const currentUndoStack = currentCanvas.undoStack;
  const currentRedoStack = currentCanvas.redoStack;


  // שאר הסטייטים
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [canvasName, setCanvasName] = useState("");

  // פונקציה לעדכון מצב הצורות בקנבס וניהול היסטוריית אנדו/רדו
  const updateCanvasShapes = (newShapes, shouldPushToUndo = true) => {
    setCanvases(prevCanvases => {
      const updatedCanvases = [...prevCanvases];
      const canvasToUpdate = { ...updatedCanvases[selectedCanvasIdx] };

      // שומרים את המצב הנוכחי של כל הצורות לפני השינוי ב-undoStack
      if (shouldPushToUndo) {
        canvasToUpdate.undoStack = [...canvasToUpdate.undoStack, canvasToUpdate.drawings.flatMap(d => d.shapes)];
        canvasToUpdate.redoStack = []; // מנקים את Redo כשמתבצעת פעולה חדשה
      }

      // מעדכנים את הצורות של הציור הראשי בקנבס
      // מכיוון שאנו רוצים לשמור על מבנה ה-drawings (שכלול description של הפרומפט),
      // אנו פשוט נעדכן את הציור האחרון במערך drawings (או ניצור חדש אם אין).
      let newDrawingsArray;
      if (canvasToUpdate.drawings.length === 0) {
        newDrawingsArray = [{ description: "ציור ראשי", shapes: newShapes }];
      } else {
        newDrawingsArray = [...canvasToUpdate.drawings];
        // מעדכנים את הצורות של הציור האחרון ב-drawings
        newDrawingsArray[newDrawingsArray.length - 1] = {
          ...newDrawingsArray[newDrawingsArray.length - 1],
          shapes: newShapes
        };
      }


      canvasToUpdate.drawings = newDrawingsArray;
      updatedCanvases[selectedCanvasIdx] = canvasToUpdate;
      return updatedCanvases;
    });
  };


  // עדכון סטייטים כאשר מחליפים קנבס
// עדכון סטייטים כאשר מחליפים קנבס
  useEffect(() => {
    // כשמחליפים קנבס, נטען את הצורות והיסטוריית אנדו/רדו של הקנבס החדש
    const canvas = canvases[selectedCanvasIdx];
    // אם הקנבס נטען מהשרת (יש לו id), ה-drawings, undoStack ו-redoStack יגיעו משם.
    // אם זה קנבס חדש/ריק, נאפס את הסטייטים.
    if (!canvas.id) {
      setCanvases(prevCanvases => {
        const newCanvases = [...prevCanvases];
        newCanvases[selectedCanvasIdx] = {
          ...newCanvases[selectedCanvasIdx],
          drawings: [{ description: "ציור ראשון", shapes: [] }],
          undoStack: [[]],
          redoStack: []
        };
        return newCanvases;
      });
    }
    setMessages([]); // מנקה היסטוריית שיחה בהחלפת קנבס
  }, [selectedCanvasIdx, canvases]); // תלויות גם בקנבסים כדי להגיב לשינויים בתוכן שלהם
  function handleClearDrawing() {
    // שומר אנדו לניקוי כל הציורים
    updateCanvasShapes([], true); // מנקה את כל הצורות ומוסיף ל-undo stack
  }

  // Undo
  function handleUndo() {
    if (currentUndoStack.length <= 1) return; // יש לפחות מצב התחלתי אחד

    setCanvases(prevCanvases => {
      const updatedCanvases = [...prevCanvases];
      const canvasToUpdate = { ...updatedCanvases[selectedCanvasIdx] };

      const prevShapes = canvasToUpdate.undoStack[canvasToUpdate.undoStack.length - 1];
      const newUndoStack = canvasToUpdate.undoStack.slice(0, -1);
      const newRedoStack = [...canvasToUpdate.redoStack, canvasToUpdate.drawings.flatMap(d => d.shapes)];

      // מעדכנים את הצורות של הציור הראשי בקנבס
      let newDrawingsArray;
      if (canvasToUpdate.drawings.length === 0) {
        newDrawingsArray = [{ description: "ציור ראשי", shapes: prevShapes }];
      } else {
        newDrawingsArray = [...canvasToUpdate.drawings];
        newDrawingsArray[newDrawingsArray.length - 1] = {
          ...newDrawingsArray[newDrawingsArray.length - 1],
          shapes: prevShapes
        };
      }

      canvasToUpdate.drawings = newDrawingsArray;
      canvasToUpdate.undoStack = newUndoStack;
      canvasToUpdate.redoStack = newRedoStack;
      updatedCanvases[selectedCanvasIdx] = canvasToUpdate;
      return updatedCanvases;
    });
  }

  function handleRedo() {
    if (currentRedoStack.length === 0) return;

    setCanvases(prevCanvases => {
      const updatedCanvases = [...prevCanvases];
      const canvasToUpdate = { ...updatedCanvases[selectedCanvasIdx] };

      const [nextShapes, ...restRedo] = canvasToUpdate.redoStack;
      const newUndoStack = [...canvasToUpdate.undoStack, canvasToUpdate.drawings.flatMap(d => d.shapes)];
      const newRedoStack = restRedo;

      // מעדכנים את הצורות של הציור הראשי בקנבס
      let newDrawingsArray;
      if (canvasToUpdate.drawings.length === 0) {
        newDrawingsArray = [{ description: "ציור ראשי", shapes: nextShapes }];
      } else {
        newDrawingsArray = [...canvasToUpdate.drawings];
        newDrawingsArray[newDrawingsArray.length - 1] = {
          ...newDrawingsArray[newDrawingsArray.length - 1],
          shapes: nextShapes
        };
      }

      canvasToUpdate.drawings = newDrawingsArray;
      canvasToUpdate.undoStack = newUndoStack;
      canvasToUpdate.redoStack = newRedoStack;
      updatedCanvases[selectedCanvasIdx] = canvasToUpdate;
      return updatedCanvases;
    });
  }

  // פתיחת דיאלוג שמירה
  function handleSave() {
    setCanvasName("");
    setShowSaveDialog(true);
  }

  // שמירת קנבס לשרת (מקבל id מהשרת)
  async function handleSaveCanvas() {
    if (!canvasName.trim()) return;
    setShowSaveDialog(false);
    try {
      const response = await fetch("/api/drawings/save-canvas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: canvasName,
          // שולחים את כל ה-drawings של הקנבס הנוכחי, כולל התיאורים והצורות שלהם
          drawings: currentDrawings
        }),
      });

      if (response.ok) {
        const saved = await response.json();
        setCanvases(prev => {
          const newCanvases = [...prev];
          // מעדכנים את הקנבס הקיים עם ה-ID והשם החדשים מהשרת,
          // או מוסיפים אותו כקנבס חדש אם לא היה לו ID קודם.
          newCanvases[selectedCanvasIdx] = {
            ...newCanvases[selectedCanvasIdx],
            id: saved.id,
            name: saved.name,
            // Drawings, undoStack, redoStack נשארים כמו שהיו
          };
          return newCanvases;
        });

        alert("הקנבס נשמר בהצלחה!");
      } else {
        alert("שגיאה בשמירת הקנבס");
      }
    } catch (err) {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  async function handleSelectCanvas(idx) {
    setSelectedCanvasIdx(idx);
    const canvas = canvases[idx];

    // אם זהו קנבס חדש ללא ID (כמו "קנבס ראשי" לפני שמירה), נאפס את מצבו המקומי
    if (canvas.id === null) {
      setCanvases(prevCanvases => {
        const newCanvases = [...prevCanvases];
        newCanvases[idx] = {
          ...newCanvases[idx],
          drawings: [{ description: "ציור ראשון", shapes: [] }],
          undoStack: [[]],
          redoStack: []
        };
        return newCanvases;
      });
      return;
    }

    try {
      const res = await fetch(`/api/drawings/get/${canvas.id}`);
      if (res.ok) {
        const data = await res.json();
        // כשקנבס נטען מהשרת, אנו מאתחלים את ה-undoStack וה-redoStack שלו.
        // ה-undoStack מתחיל עם המצב הנוכחי של הצורות כנקודת התחלה.
        setCanvases(prevCanvases => {
          const newCanvases = [...prevCanvases];
          newCanvases[idx] = {
            ...newCanvases[idx],
            drawings: data.drawings, // טוענים את הציורים שהגיעו מהשרת
            undoStack: [data.drawings.flatMap(d => d.shapes)], // מצב התחלתי לאנדו
            redoStack: []
          };
          return newCanvases;
        });
      } else {
        alert("שגיאה בטעינת הקנבס");
      }
    } catch {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  // שליחת הודעה לשרת
  async function handleSend() {
    if (!prompt.trim()) return;
    setMessages([...messages, { from: "user", text: prompt }]);
    setLoading(true);

    try {
      const response = await fetch("/api/drawings/add-draw", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt,
          existingDrawings: currentDrawings // שולחים את כל הציורים הקיימים
        }),
      });

      if (!response.ok) {
        setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בשליחת הבקשה לשרת" }]);
        setLoading(false);
        return;
      }

      const newShapes = await response.json();

      setCanvases(prevCanvases => {
        const updatedCanvases = [...prevCanvases];
        const canvasToUpdate = { ...updatedCanvases[selectedCanvasIdx] };

        // שומרים את המצב הנוכחי של כל הצורות לפני הוספת החדשות ל-undoStack
        canvasToUpdate.undoStack = [...canvasToUpdate.undoStack, canvasToUpdate.drawings.flatMap(d => d.shapes)];
        canvasToUpdate.redoStack = []; // מנקים את Redo כשמתבצעת פעולה חדשה

        // מוסיפים את הציור החדש למערך ה-drawings של הקנבס הנוכחי
        canvasToUpdate.drawings = [
          ...canvasToUpdate.drawings,
          {
            description: prompt,
            shapes: newShapes
          }
        ];

        updatedCanvases[selectedCanvasIdx] = canvasToUpdate;
        return updatedCanvases;
      });

      setMessages(msgs => [...msgs, { from: "bot", text: "הציור התעדכן בהצלחה!" }]);
      setPrompt("");
    } catch (err) {
      setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בתקשורת עם השרת" }]);
    }
    setLoading(false);
  }

  return (
    <div className="app-container">
      <div className="toolbar">
        {/* בחירת קנבס אחד בלבד */}
        <select value={selectedCanvasIdx} onChange={e => handleSelectCanvas(Number(e.target.value))}>
          {canvases.map((canvas, idx) => (
            <option key={canvas.id ?? `canvas-${idx}`} value={idx}>{canvas.name}</option>
          ))}
        </select>
        <button
          className="toolbar-btn orange"
          onClick={handleUndo}
          disabled={currentUndoStack.length <= 1} // משבית אם אין מצבים קודמים
        >
          אנדו
        </button>
        <button
          className="toolbar-btn purple"
          onClick={handleRedo}
          disabled={currentRedoStack.length === 0} // משבית אם אין מצבים עתידיים
        >
          רדו
        </button>
        {/* כפתור ניקוי ציור */}
        <button className="toolbar-btn red" onClick={handleClearDrawing}>ניקוי לוח</button>

        <button className="toolbar-btn green" onClick={handleSave}>סייב</button>
      </div>
      <div className="main-content">
        <div className="chat-section">
          <div className="chat-title">היסטוריית שיחה</div>
          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div key={i} className={`chat-msg ${msg.from}`}>
                <b>{msg.from === "user" ? "אתה:" : "בוט:"}</b> {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-row">
            <input
              type="text"
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              placeholder="כתוב הודעה לבוט..."
              disabled={loading}
              onKeyDown={e => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend} disabled={loading || !prompt.trim()}>שלח</button>
          </div>
        </div>
        <div className="canvas-section">
          {loading && <div className="loading-indicator">מצייר לך...</div>}
          <svg width="600" height="400">
            {/* מציג את כל הצורות של כל הציורים יחד על הקנבס הנוכחי */}
            {currentShapes.map((shape, i) => renderShape(shape, i))}
          </svg>
        </div>
      </div>

      {/* דיאלוג שמירה */}
      {showSaveDialog && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000
        }}>
          <div style={{
            background: "#fff", padding: 30, borderRadius: 10, boxShadow: "0 2px 12px #0002", minWidth: 300
          }}>
            <div style={{ marginBottom: 10, fontWeight: "bold" }}>שם הקנבס:</div>
            <input
              type="text"
              value={canvasName}
              onChange={e => setCanvasName(e.target.value)}
              placeholder="הכנס שם לקנבס"
              style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc", marginBottom: 16 }}
              autoFocus
            />
            <div style={{ display: "flex", gap: 10, justifyContent: "flex-end" }}>
              <button className="toolbar-btn green" onClick={handleSaveCanvas} disabled={!canvasName.trim()}>שמור</button>
              <button className="toolbar-btn orange" onClick={() => setShowSaveDialog(false)}>ביטול</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
