          // מצא את האינדקס של "קנבס ראשי" אם הוא קיים
          const mainCanvasIdx = prev.findIndex(canvas => canvas.id === null && canvas.name === "קנבס ראשי");
          let updatedCanvases = [...prev];

          // אם הקנבס שנשמר היה "קנבס ראשי" ללא ID, נעדכן אותו עם ה-ID והשם החדשים
