        setCanvases(prev => {
          const foundIdx = prev.findIndex(canvas => canvas.id === saved.id);
          if (foundIdx !== -1) {
            setSelectedCanvasIdx(foundIdx);
          } else {
            setSelectedCanvasIdx(prev.length); // אם הוא נוסף בסוף
          }
          return prev; // לא משנים את prev כאן, זה כבר עודכן בסטייט למעלה
        });
        alert("הקנבס נשמר בהצלחה!");
      } else {
        alert("שגיאה בשמירת הקנבס");
