import React, { useState, useEffect } from "react";
import "./App.css";

function renderShape(shape, i) {
  const { type, x, y, width, height, color } = shape;
  switch (type) {
    case "Rectangle":
      return <rect key={i} x={x} y={y} width={width} height={height} fill={color} stroke="#333" />;
    case "Square":
      return <rect key={i} x={x} y={y} width={width} height={width} fill={color} stroke="#333" />;
    case "Circle":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + width / 2}
          rx={width / 2}
          ry={width / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Ellipse":
      return (
        <ellipse
          key={i}
          cx={x + width / 2}
          cy={y + height / 2}
          rx={width / 2}
          ry={height / 2}
          fill={color}
          stroke="#333"
        />
      );
    case "Line":
      return <line key={i} x1={x} y1={y} x2={x + width} y2={y + height} stroke={color} strokeWidth="3" />;
    case "Triangle":
      return (
        <polygon
          key={i}
          points={`
            ${x + width / 2},${y}
            ${x},${y + height}
            ${x + width},${y + height}
          `}
          fill={color}
          stroke="#333"
        />
      );
    default:
      return null;
  }
}

export default function App() {
  const [canvases, setCanvases] = useState([
    { id: null, name: "קנבס ראשי", drawings: [{ description: "ציור ראשון", shapes: [] }] }
  ]);
  const [selectedCanvasIdx, setSelectedCanvasIdx] = useState(0);

  // הסטייטים האלה יכילו כעת את המידע **הנוכחי** של הקנבס הנבחר
  // ונוודא שהם תמיד משקפים את המצב של drawings[0] עבור אנדו/רדו.
  const [drawings, setDrawings] = useState(canvases[0].drawings);

  // Undo ו-Redo הם עבור כלל הצורות בקנבס הנבחר
  // ננהל סטייקים גלובליים לכל הקנבס, המייצגים את כלל הצורות
  const [undoStack, setUndoStack] = useState([[]]); // מתחיל עם מצב ריק (אין צורות)
  const [redoStack, setRedoStack] = useState([]);

  // שאר הסטייטים
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showSaveDialog, setShowSaveDialog] = useState(false);
  const [canvasName, setCanvasName] = useState("");

  // פונקציה לעדכון מצב הקנבס (צורות) וניהול היסטוריית אנדו/רדו
  // מקבלת את מערך הצורות החדש כפרמטר
  const updateCanvasWithNewShapes = (newShapes, shouldPushToUndo = true) => {
    setDrawings(prevDrawings => {
      // אם זהו המצב הראשון אי פעם (או אחרי אתחול), או אם יש רק ציור אחד, נעדכן אותו.
      // אחרת, אנחנו צריכים לשטח את כל הצורות הקיימות ולהחליף אותן במערך newShapes.
      // זה מבטיח שכל הצורות מכל הציורים הקודמים יוצגו כחלק מציור אחד ראשי
      // לצורך אנדו/רדו גלובלי.
      const updatedDrawings = [{ description: "ציור ראשי", shapes: newShapes }];

      // נשמור את המצב הנוכחי של **כלל הצורות בקנבס** לפני השינוי
      if (shouldPushToUndo) {
        const currentFlatShapes = prevDrawings.flatMap(d => d.shapes);
        // נדחוף רק אם המצב הנוכחי שונה מהמצב החדש
        if (JSON.stringify(currentFlatShapes) !== JSON.stringify(newShapes)) {
          setUndoStack(prevUndo => [...prevUndo, currentFlatShapes]);
          setRedoStack([]); // מנקה את Redo כשמתבצעת פעולה חדשה
        }
      }
      return updatedDrawings;
    });
  };

  // עדכון סטייטים כאשר מחליפים קנבס
  useEffect(() => {
    const canvasData = canvases[selectedCanvasIdx];
    // נעדכן את הסטייט של drawings למערך ה-shapes המאוחד של הקנבס הנבחר
    // וכן נאפס את ערימות האנדו/רדו למצב ההתחלתי של הקנבס
    setDrawings(canvasData.drawings);
    setUndoStack([canvasData.drawings.flatMap(d => d.shapes)]); // המצב הנוכחי כנקודת התחלה לאנדו
    setRedoStack([]);
    setMessages([]); // מנקה היסטוריית שיחה בהחלפת קנבס
  }, [selectedCanvasIdx, canvases]); // תלויות גם בקנבסים כדי להגיב לשינויים בתוכן שלהם

  function handleClearDrawing() {
    // לפני הניקוי, נדחוף את כל הצורות הנוכחיות ל-undo stack.
    // מכיוון שאנחנו רוצים לנקות את *כל* הציורים, אנחנו משתמשים ב-flatMap כדי לאסוף את כולם.
    setUndoStack(prevUndo => [...prevUndo, drawings.flatMap(d => d.shapes)]);
    setRedoStack([]); // מנקים את Redo כשמנקים את הלוח

    // מאפסים את ה-drawings למצב של ציור ראשי ריק
    setDrawings([{ description: "ציור ראשון", shapes: [] }]);
  }

  // Undo
  function handleUndo() {
    // console.log("Undo Stack before:", undoStack);

    if (undoStack.length <= 1) return; // אין מצבים קודמים (מצב התחלתי הוא המצב היחיד)

    setUndoStack(prevUndo => {
      const currentShapesState = drawings.flatMap(d => d.shapes); // המצב הנוכחי
      const newUndo = prevUndo.slice(0, -1); // מוריד את המצב הנוכחי

      const prevShapes = newUndo[newUndo.length - 1]; // המצב הקודם

      // מוסיף את המצב הנוכחי (לפני האנדו) ל-redo stack
      setRedoStack(prevRedo => [currentShapesState, ...prevRedo]);

      // מעדכן את ה-drawings להיות המצב הקודם
      setDrawings([{ description: "ציור ראשי", shapes: prevShapes }]);

      return newUndo;
    });
  }

  function handleRedo() {
    // console.log("Redo Stack before:", redoStack);

    if (redoStack.length === 0) return;

    setRedoStack(prevRedo => {
      const [nextShapes, ...restRedo] = prevRedo; // המצב הבא
      const currentShapesState = drawings.flatMap(d => d.shapes); // המצב הנוכחי

      // מוסיף את המצב הנוכחי (לפני הרדו) ל-undo stack
      setUndoStack(prevUndo => [...prevUndo, currentShapesState]);

      // מעדכן את ה-drawings להיות המצב הבא
      setDrawings([{ description: "ציור ראשי", shapes: nextShapes }]);

      return restRedo;
    });
  }

  // פתיחת דיאלוג שמירה
  function handleSave() {
    setCanvasName("");
    setShowSaveDialog(true);
  }

  // שמירת קנבס לשרת (מקבל id מהשרת)
  async function handleSaveCanvas() {
    if (!canvasName.trim()) return;
    setShowSaveDialog(false);
    try {
      const response = await fetch("/api/drawings/save-canvas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: canvasName,
          // בעת שמירה, אנו רוצים לשלוח את כל הצורות הנראות כרגע על הקנבס.
          // Drawings מכיל כבר את כל הצורות המאוחדות כחלק מהציור הראשי.
          // אם בכל זאת יש מספר אובייקטים ב-drawings, נשלח את כולם.
          drawings: drawings // שולחים את המצב הנוכחי של ה-drawings
        })
      });

      if (response.ok) {
        const saved = await response.json();
        // יוצרים קנבס חדש במערך ה-canvases
        const newCanvas = {
          id: saved.id,
          name: saved.name,
          drawings: drawings, // שומרים את ה-drawings הנוכחיים של הקנבס שנשמר
        };

        setCanvases(prev => {
          // מצא את האינדקס של "קנבס ראשי" אם הוא קיים
          const mainCanvasIdx = prev.findIndex(canvas => canvas.id === null && canvas.name === "קנבס ראשי");
          let updatedCanvases = [...prev];

          // אם הקנבס שנשמר היה "קנבס ראשי" ללא ID, נעדכן אותו עם ה-ID והשם החדשים
          if (selectedCanvasIdx === mainCanvasIdx && newCanvas.id !== null) {
            updatedCanvases[selectedCanvasIdx] = {
              ...updatedCanvases[selectedCanvasIdx],
              id: newCanvas.id,
              name: newCanvas.name,
              drawings: newCanvas.drawings,
            };
          } else {
            // אחרת, נוסיף אותו כקנבס חדש לגמרי
            updatedCanvases = [...prev, newCanvas];
          }
          return updatedCanvases;
        });

        // ייתכן שנרצה לעבור לקנבס החדש שנשמר
        // מכיוון ש-setCanvases הוא אסינכרוני, נצטרך למצוא את האינדקס החדש
        // של הקנבס שנשמר כדי לבחור אותו.
        setCanvases(prev => {
          const foundIdx = prev.findIndex(canvas => canvas.id === saved.id);
          if (foundIdx !== -1) {
            setSelectedCanvasIdx(foundIdx);
          } else {
            setSelectedCanvasIdx(prev.length); // אם הוא נוסף בסוף
          }
          return prev; // לא משנים את prev כאן, זה כבר עודכן בסטייט למעלה
        });


        alert("הקנבס נשמר בהצלחה!");
      } else {
        alert("שגיאה בשמירת הקנבס");
      }
    } catch (err) {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  async function handleSelectCanvas(idx) {
    setSelectedCanvasIdx(idx);
    const canvas = canvases[idx];

    // אם זה קנבס חדש (ללא ID)
    if (!canvas.id) {
      setDrawings(canvas.drawings);
      setUndoStack([canvas.drawings.flatMap(d => d.shapes)]);
      setRedoStack([]);
      setMessages([]);
      return;
    }

    // אם זה קנבס קיים מהשרת
    try {
      const res = await fetch(`/api/drawings/get/${canvas.id}`);
      if (res.ok) {
        const data = await res.json();
        setDrawings(data.drawings);
        // מאתחלים את ערימות האנדו/רדו עם המצב של הצורות מהקנבס שנטען
        setUndoStack([data.drawings.flatMap(d => d.shapes)]);
        setRedoStack([]);
        setMessages([]);
      } else {
        alert("שגיאה בטעינת הקנבס");
      }
    } catch {
      alert("שגיאה בתקשורת עם השרת");
    }
  }

  // שליחת הודעה לשרת
  async function handleSend() {
    if (!prompt.trim()) return;
    setMessages([...messages, { from: "user", text: prompt }]);
    setLoading(true);

    try {
      const response = await fetch("/api/drawings/add-draw", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt,
          existingDrawings: drawings // שולחים את ה-drawings הנוכחיים של הקנבס
        })
      });

      if (!response.ok) {
        setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בשליחת הבקשה לשרת" }]);
        setLoading(false);
        return;
      }

      const newShapes = await response.json();

      // לפני עדכון ה-drawings, נשמור את המצב הנוכחי באנדו סטאק
      setUndoStack(prevUndo => [...prevUndo, drawings.flatMap(d => d.shapes)]);
      setRedoStack([]); // מנקים את Redo כשמתבצעת פעולה חדשה

      // מוסיפים את הצורות החדשות לציור הראשי (drawings[0])
      setDrawings(prev => {
        // יוצרים ציור חדש עם התיאור והצורות שהתקבלו
        const newDrawing = {
          description: prompt,
          shapes: newShapes
        };
        // מוסיפים את הציור החדש למערך ה-drawings
        // כלומר, כל פרומפט ייצור "ציור" חדש במערך drawings
        return [...prev, newDrawing];
      });

      setMessages(msgs => [...msgs, { from: "bot", text: "הציור התעדכן בהצלחה!" }]);
      setPrompt("");
    } catch (err) {
      setMessages(msgs => [...msgs, { from: "bot", text: "שגיאה בתקשורת עם השרת" }]);
    }
    setLoading(false);
  }

  return (
    <div className="app-container">
      <div className="toolbar">
        {/* בחירת קנבס אחד בלבד */}
        <select value={selectedCanvasIdx} onChange={e => handleSelectCanvas(Number(e.target.value))}>
          {canvases.map((canvas, idx) => (
            <option key={canvas.id ?? idx} value={idx}>{canvas.name}</option>
          ))}
        </select>
        <button
          className="toolbar-btn orange"
          onClick={handleUndo}
          // נשבית אנדו אם יש רק מצב אחד (המצב ההתחלתי)
          disabled={undoStack.length <= 1}
        >
          אנדו
        </button>
        <button
          className="toolbar-btn purple"
          onClick={handleRedo}
          // נשבית רדו אם אין מצבים בערימת הרדו
          disabled={redoStack.length === 0}
        >
          רדו
        </button>
        {/* כפתור ניקוי ציור */}
        <button className="toolbar-btn red" onClick={handleClearDrawing}>ניקוי לוח</button>

        <button className="toolbar-btn green" onClick={handleSave}>סייב</button>
      </div>
      <div className="main-content">
        <div className="chat-section">
          <div className="chat-title">היסטוריית שיחה</div>
          <div className="chat-messages">
            {messages.map((msg, i) => (
              <div key={i} className={`chat-msg ${msg.from}`}>
                <b>{msg.from === "user" ? "אתה:" : "בוט:"}</b> {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-row">
            <input
              type="text"
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              placeholder="כתוב הודעה לבוט..."
              disabled={loading}
              onKeyDown={e => e.key === "Enter" && handleSend()}
            />
            <button onClick={handleSend} disabled={loading || !prompt.trim()}>שלח</button>
          </div>
        </div>
        <div className="canvas-section">
          {loading && <div className="loading-indicator">מצייר לך...</div>}
          <svg width="600" height="400">
            {/* מציג את כל הצורות מכל הציורים במערך drawings */}
            {drawings.flatMap((drawing, dIdx) =>
              drawing.shapes.map((shape, i) => renderShape(shape, `${dIdx}-${i}`))
            )}
          </svg>
        </div>
      </div>

      {/* דיאלוג שמירה */}
      {showSaveDialog && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000
        }}>
          <div style={{
            background: "#fff", padding: 30, borderRadius: 10, boxShadow: "0 2px 12px #0002", minWidth: 300
          }}>
            <div style={{ marginBottom: 10, fontWeight: "bold" }}>שם הקנבס:</div>
            <input
              type="text"
              value={canvasName}
              onChange={e => setCanvasName(e.target.value)}
              placeholder="הכנס שם לקנבס"
              style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc", marginBottom: 16 }}
              autoFocus
            />
            <div style={{ display: "flex", gap: 10, justifyContent: "flex-end" }}>
              <button className="toolbar-btn green" onClick={handleSaveCanvas} disabled={!canvasName.trim()}>שמור</button>
              <button className="toolbar-btn orange" onClick={() => setShowSaveDialog(false)}>ביטול</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
